import 'dart:convert';
import 'dart:typed_data';

import 'dart:ui';

import 'package:umbra_flutter/umbra_flutter.dart';

/// {@template gradient_shader}
/// A Dart Shader class for the `gradient_shader` shader.
/// {@endtemplate}
class GradientShader extends UmbraShader {
  GradientShader._() : super(_cachedProgram!);

  /// {@macro gradient_shader}
  static Future<GradientShader> compile() async {
    try {
      // Caching the program on the first compile call.
      _cachedProgram ??= await FragmentProgram.compile(
        spirv: Uint8List.fromList(base64Decode(_spirv)).buffer,
      );

      return GradientShader._();
    } on Exception catch (err, stackTrace) {
      throw UmbraException(err, stackTrace);
    }
  }

  static FragmentProgram? _cachedProgram;

  Shader shader({
    required Size resolution,
    required double uTime,
    required double speed,
    required Vector2 uResolution,
  }) {
    return program.shader(
      floatUniforms: Float32List.fromList([
        uTime,
        speed,
        uResolution.x,
        uResolution.y,
        resolution.width,
        resolution.height,
      ]),
      samplerUniforms: [],
    );
  }
}

const _spirv =
    '';
